package 进制转换;

public class RadixMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		System.out.println("9转换为二进制为：" + Integer.toBinaryString(a));
		System.out.println("9转换为八进制为：" + Integer.toOctalString(a));
		System.out.println("9转换为十六进制为：" + Integer.toHexString(a));

	}

}
